package com.mouni;
import java.util.*;

public class SortStrings {

	public static void main(String[] args) {
	
		        String[] names = {"John", "Alice", "Bob", "Charlie", "Eve"};

		        
		        Arrays.sort(names);

		        
		        System.out.println("Sorted names:");
		        for (String name : names) {
		            System.out.println(name);
		        }
		    }
		}

